Activity_0ygriuv.script:
# Extract Departments and Divisions from Bamboo Metadata
departments_and_divisions = {
    field["name"]: field["options"]
    for field in bamboo_list_metadata
    if field["name"] in ["Department", "Division"]
}

# Delete Metadata
del(bamboo_list_metadata)

# Extract Unigue list of Employee Departments from Bamboo Custome report
employee_departments = [*set([x["department"] for x in resp_DivDept["employees"] if x["department"] != None])]

# Delete Custom Report Data
del(resp_DivDept)

# Check if Get Extermal Data variables have been passed in and if not, set them to False
# This allow the process to be run locally for testing
try:
  is_bamboo_dept_list
except NameError:
  is_bamboo_dept_list = False

try:
  is_bamboo_dept_enum_list
except NameError:
  is_bamboo_dept_enum_list = False

# Separate Depoartment and Division lists, as needed
if is_bamboo_dept_list or is_bamboo_dept_enum_list:
    departments = departments_and_divisions.get("Department", [])
    dept_list = [{"id": x["id"], "name": x["name"]} for x in departments if x["archived"] != "yes"]

    if is_bamboo_dept_enum_list:
        dept_enum_list = [{"label": y["name"], "value": str(y["id"])} for y in dept_list if y["name"] in employee_departments]

    if not(is_bamboo_dept_list):
        del(dept_list)

    if not(is_bamboo_dept):
        del(departments)


try:
  is_bamboo_div_list
except NameError:
  is_bamboo_div_list = False

try:
  is_bamboo_div_enum_list
except NameError:
  is_bamboo_div_enum_list = False


if is_bamboo_div_list or is_bamboo_div_enum_list:
    divisions = departments_and_divisions.get("Division", [])
    div_list = [{"id": x["id"], "name": x["name"]} for x in divisions]

    if is_bamboo_div_enum_list:
        div_enum_list = [{"label": y["name"], "value": str(y["id"])} for y in div_list]

    if not(is_bamboo_div_list):
        del(div_list)

    if not(is_bamboo_div):
        del(divisions)

# Delete Unneeded Variables
del(departments_and_divisions)
del(employee_departments)
del(is_bamboo_dept)
del(is_bamboo_dept_list)
del(is_bamboo_dept_enum_list)
del(is_bamboo_div)
del(is_bamboo_div_list)
del(is_bamboo_div_enum_list)

